import * as THREE from 'three';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader.js';
import { GPUMonitor } from './GPUMonitor';  // Ensure implemented or stubbed

export function createModelScene(mountNode) {
  const scene = new THREE.Scene();
  scene.background = new THREE.Color(0xdddddd);  // Soft gray background

  const camera = new THREE.PerspectiveCamera(
    60,
    mountNode.clientWidth / mountNode.clientHeight,
    0.1,
    1000
  );
  camera.position.set(0, 1.5, 4);

  const renderer = new THREE.WebGLRenderer({ antialias: true, powerPreference: 'low-power' });
  renderer.shadowMap.enabled = true;
  renderer.shadowMap.type = THREE.PCFSoftShadowMap;
  renderer.setPixelRatio(Math.min(window.devicePixelRatio || 1, 1.5));
  renderer.setSize(mountNode.clientWidth, mountNode.clientHeight);
  mountNode.appendChild(renderer.domElement);

  const gpuMonitor = new GPUMonitor(renderer);

  // Lighting Setup
  const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
  const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
  directionalLight.position.set(5, 10, 7);
  directionalLight.castShadow = true;
  directionalLight.shadow.mapSize.width = 1024;
  directionalLight.shadow.mapSize.height = 1024;
  directionalLight.shadow.camera.near = 0.5;
  directionalLight.shadow.camera.far = 50;

  scene.add(ambientLight);
  scene.add(directionalLight);

  // Add a soft ground plane
  const planeGeometry = new THREE.PlaneGeometry(10, 10);
  const planeMaterial = new THREE.MeshStandardMaterial({ color: 0x888888, roughness: 0.8, metalness: 0.2 });
  const plane = new THREE.Mesh(planeGeometry, planeMaterial);
  plane.rotation.x = -Math.PI / 2;
  plane.receiveShadow = true;
  scene.add(plane);

  // Load car model
  const loader = new GLTFLoader();
  let model = null;
  let isModelLoaded = false;

  loader.load(
    '/models/car.glb',  // Ensure correct path
    (gltf) => {
      model = gltf.scene;
      model.scale.set(1.2, 1.2, 1.2);
      model.traverse((child) => {
        if (child.isMesh) {
          child.castShadow = true;
          child.receiveShadow = true;

          // Enhance material color for better visibility
          if (child.material) {
            child.material.metalness = 0.7;
            child.material.roughness = 0.4;
          }
        }
      });
      scene.add(model);
      isModelLoaded = true;
    },
    undefined,
    (error) => console.error('Model load error:', error)
  );

  // Overlay UI
  const overlay = document.createElement('div');
  overlay.style.position = 'absolute';
  overlay.style.top = '10px';
  overlay.style.left = '10px';
  overlay.style.padding = '10px';
  overlay.style.borderRadius = '12px';
  overlay.style.fontFamily = 'Arial, sans-serif';
  overlay.style.fontSize = '14px';
  overlay.style.background = 'rgba(255,255,255,0.9)';
  overlay.style.boxShadow = '0 4px 10px rgba(0,0,0,0.2)';
  overlay.style.zIndex = '10';
  overlay.innerHTML = `ðŸŒ± Carbon Footprint: <b>1.2 kg COâ‚‚e</b>`;
  mountNode.style.position = 'relative';
  mountNode.appendChild(overlay);

  // Interaction for 360Â° view
  let isDragging = false;
  let prevX = 0;
  let prevY = 0;

  function onPointerDown(e) {
    isDragging = true;
    prevX = e.clientX;
    prevY = e.clientY;
  }

  function onPointerMove(e) {
    if (!isDragging || !isModelLoaded) return;

    const deltaX = e.clientX - prevX;
    const deltaY = e.clientY - prevY;

    model.rotation.y += deltaX * 0.005;
    model.rotation.x += deltaY * 0.005;

    // Clamp vertical rotation
    model.rotation.x = Math.max(-Math.PI / 2, Math.min(Math.PI / 2, model.rotation.x));

    prevX = e.clientX;
    prevY = e.clientY;
  }

  function onPointerUp() {
    isDragging = false;
  }

  renderer.domElement.addEventListener('pointerdown', onPointerDown);
  renderer.domElement.addEventListener('pointermove', onPointerMove);
  renderer.domElement.addEventListener('pointerup', onPointerUp);
  renderer.domElement.addEventListener('pointerleave', onPointerUp);

  // Animation loop
  function animate(now) {
    renderer.render(scene, camera);
    if (gpuMonitor && typeof gpuMonitor.onFrameRendered === 'function') {
      gpuMonitor.onFrameRendered(now);
    }
    requestAnimationFrame(animate);
  }

  requestAnimationFrame(animate);

  function cleanup() {
    renderer.domElement.removeEventListener('pointerdown', onPointerDown);
    renderer.domElement.removeEventListener('pointermove', onPointerMove);
    renderer.domElement.removeEventListener('pointerup', onPointerUp);
    renderer.domElement.removeEventListener('pointerleave', onPointerUp);

    if (model) {
      model.traverse((child) => {
        if (child.geometry) child.geometry.dispose();
        if (child.material) child.material.dispose();
      });
    }

    gpuMonitor.destroy();
    renderer.dispose();

    if (mountNode) {
      mountNode.removeChild(renderer.domElement);
    }

    if (overlay && overlay.parentNode) {
      overlay.parentNode.removeChild(overlay);
    }
  }

  return { cleanup, gpuMonitor, scene, renderer };
};
